readContactIDs()

public void readContactIDs()
{
	def filePath = System.getProperty("user.dir") + "//ContactIDs.txt"
	BufferedReader reader;
	ArrayList<String> contactIDs = new ArrayList<String>();
	try{
		reader = new BufferedReader(new FileReader(filePath.toString().trim()))
		String line = reader.readLine()
		while (line != null){
			contactIDs.add(line)
			line = reader.readLine()
		}
		reader.close()
	}
	catch(IOException ex){
		e.printStackTrace()
	}
	updateContacts(contactIDs)
}

public void updateContacts(ArrayList<String> contactIDs)
{
	String strFolderPath = System.getProperty("user.dir") + "//ContactIDs.xls"
	FileInputStream fis = null;
	HSSFWorkbook workbook = null;
	HSSFSheet worksheet = null;
	String[] columns = new String[3]	
	columns[0]="CONTACTS_ID"
	columns[1]="MATCHING_ID"
	columns[2]="RESULT"

	if (contactIDs.size() == 0)
	return;

	File file = new File(strFolderPath);
	if (!file.exists()){
		/*try{
			fis = new FileInputStream(new File(strFolderPath))
			workbook = new HSSFWorkbook(fis)
			worksheet = workbook.createSheet(strSuite);
		}catch(IOException ex){
			log.info(ex.getMessage())
		}*/
		workbook = new HSSFWorkbook()
		worksheet = workbook.createSheet("ContactIDs");		
	}
	// Header Row
	HSSFRow headerRow = worksheet.createRow(0)
	for (int idx=0; idx < columns.length; idx++){
		org.apache.poi.hssf.usermodel.HSSFCell cell = headerRow.createCell(idx)
		cell.setCellValue(columns[idx]);
	}

	// Add RowData
	int rowNum=0;
	for(int x=0; x <contactIDs.size(); x++ ){
		HSSFRow row = worksheet.createRow(++rowNum)
		row.createCell(0).setCellValue(contactIDs.get(x))
		row.createCell(1).setCellValue(0)
		row.createCell(2).setCellValue("Test")
	}

	// AutoSize Columns
	for(int i=0; i < columns.length; i++){
		worksheet.autoSizeColumn((short)i)
	}
	FileOutputStream fout = new FileOutputStream(new File(strFolderPath))
	workbook.write(fout)
	fout.close()

	log.info("File created.")
}

