def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )

def ContactID = responseHolder.getNodeValue("//*:contactResponse/*:contactId")
def addressLine = requestHolder.getNodeValue("//*:change/*:line")
def city = requestHolder.getNodeValue("//*:change/*:city")
def stateProvince = requestHolder.getNodeValue("//*:change/*:stateProvince")
def postalText = requestHolder.getNodeValue("//*:change/*:postalText")
def phoneNo = requestHolder.getNodeValue("//*:change/*:number")

messageExchange.modelItem.testStep.testCase.testSuite.setPropertyValue("TC4_ADDR_LINE",addressLine)
messageExchange.modelItem.testStep.testCase.testSuite.setPropertyValue("TC4_CITY",city)
messageExchange.modelItem.testStep.testCase.testSuite.setPropertyValue("TC4_STATE",stateProvince)
messageExchange.modelItem.testStep.testCase.testSuite.setPropertyValue("TC4_POSTAL_CODE",postalText)
messageExchange.modelItem.testStep.testCase.testSuite.setPropertyValue("TC4_PHONE_NO",phoneNo)

assert ContactID != null

FileWriter writer = new FileWriter(new File(System.getProperty("user.dir") + "//Contacts_From_WebService_To_MDM.txt"),true);
writer.write(ContactID.toString() + "\n");
writer.close();

