def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def requestHolder = groovyUtils.getXmlHolder( messageExchange.requestContent )
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )
def contactId = responseHolder.getNodeValue("//*:searchResult/*:contactId")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")
def addressLine = responseHolder.getNodeValue("//*:address/*:line")
def city = responseHolder.getNodeValue("//*:address/*:city")
def stateProvince = responseHolder.getNodeValue("//*:address/*:stateProvince")
//def postalCode = responseHolder.getNodeValue("//*:address/*:postalText")
def phoneNo = responseHolder.getNodeValue("//*:telecommunication/*:number")
