import javax.jms.*
import groovy.io.*
import java.util.Map
import java.util.List
import org.slf4j.Logger
import java.util.Properties
import javax.naming.Context
import org.slf4j.LoggerFactory
import groovy.json.JsonSlurper
import javax.naming.InitialContext
import com.eviware.soapui.support.XmlHolder
import java.util.concurrent.ExecutionException;
//------------------------------------------------------------------------------------------------------------------------------
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def requestHolder = groovyUtils.getXmlHolder( messageExchange.requestContent )
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )
def firstNames = responseHolder.getNodeValues("//*:personInfo/*:firstName")
def lastNames = responseHolder.getNodeValues("//*:personInfo/*:lastName")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")

boolean recordTypeMatch=false
boolean fnameMatch=false
boolean lnameMatch=false
boolean finalAssert=false

if (recordTypes.size()==2){
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("GOLDEN")){
			recordTypeMatch=true
			break;
		}
	}
}
if (firstNames.size()==2){
	for (int x=0; x<firstNames.size(); x++){
		if (firstNames[x].toString().equals(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC15_FNAME"))){
			fnameMatch=true
		}
	}
}
if (lastNames.size()==2){
	for (int x=0; x<lastNames.size(); x++){
		if (lastNames[x].toString().equals(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC15_LNAME"))){
			lnameMatch=true
		}
	}
}
if (recordTypeMatch==true && fnameMatch==true && lnameMatch==true){
	finalAssert=true
}
assert finalAssert == true

