def xrefContactIDs = (responseHolder != null) ? responseHolder.getNodeValues("//*:Row/*:PKEY_SRC_OBJECT") : "No Data Found"
def xrefRowObjId = (responseHolder != null) ? responseHolder.getNodeValue("//*:Row/*:ROWID_OBJECT") : "No Data Found"
messageExchange.modelItem.testStep.testCase.testSuite.project.setPropertyValue("ROWID_OBJ",xrefRowObjId)

boolean contactID1 = false
boolean contactID2 = false
if (xrefContactIDs.size()==2){
	for(int i=0; i<xrefContactIDs.size(); i++){
		if (xrefContactIDs[i].equals(messageExchange.modelItem.testStep.testCase.testSuite.project.getPropertyValue("TC17_1_CONTACT_ID"))){
			contactID1=true
			break
		}
	}
	for(int i=0; i<xrefContactIDs.size(); i++)	{
		if (xrefContactIDs[i].equals(messageExchange.modelItem.testStep.testCase.testSuite.project.getPropertyValue("TC17_2_CONTACT_ID"))){
			contactID2=true
			break
		}
	}	
}
assert contactID1 == contactID2

