import java.util.concurrent.ExecutionException
import com.datastax.driver.dse.graph.GraphNode 
import com.datastax.driver.dse.graph.GraphOptions 
import com.datastax.driver.dse.graph.GraphProtocol 
import com.datastax.driver.dse.graph.GraphResultSet
import com.datastax.driver.dse.graph.GraphStatement
import org.apache.tinkerpop.gremlin.process.traversal.dsl.graph.__; 
import org.apache.tinkerpop.gremlin.process.traversal.dsl.graph.GraphTraversal;
import org.apache.tinkerpop.gremlin.process.traversal.dsl.graph.GraphTraversalSource;

def DseSession _dseSession; 
def GraphTraversalSource g;
def graphName = testRunner.testCase.testSuite.project.getPropertyValue("GRAPH_ENV");
def username  ="ecdc_gold_l1_app";
def password  ="ecdcgoldl1app";

DseCluster dseCluster = DseCluster.builder().addContactPoint("uca00007.ute.fedex.com").withSSL().withCredentials(username, password).withGraphOptions(new GraphOptions().setGraphName(graphName).setGraphSubProtocol(GraphProtocol.GRAPHSON_2_0)).build(); 
_dseSession = dseCluster.connect(); 
g = DseGraph.traversal(_dseSession); 

List results = g.V().has("social_media","social_media_handle_desc",testRunner.testCase.testSuite.project.getPropertyValue("SOCIAL_MEDIA_VALUE")).in().toList() 
String jsonString = results[0]

def internalPartyId = results[0].id.internal_party_id.toString()
List listOrgs = g.V().has("party","internal_party_id",internalPartyId).out("has_social_media_account").toList() 
String socialMediaDesc = listOrgs[0].id.social_media_handle_desc.toString()

String Custom_socialMediaDesc = testRunner.testCase.testSuite.project.getPropertyValue("SOCIAL_MEDIA_VALUE") 
testRunner.testCase.testSuite.project.setPropertyValue("TC23_2_CONTACT_ID",internalPartyId)
log.info("Internal PartyId :" + internalPartyId)
log.info (socialMediaDesc)
dseCluster.close();

