import javax.jms.*
import groovy.io.*
import java.util.Map
import java.util.List
import org.slf4j.Logger
import java.util.Properties
import javax.naming.Context
import org.slf4j.LoggerFactory
import groovy.json.JsonSlurper
import javax.naming.InitialContext
import com.eviware.soapui.support.XmlHolder
import java.util.concurrent.ExecutionException;

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def requestHolder = groovyUtils.getXmlHolder( messageExchange.requestContent )
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )
def contactIDs = responseHolder.getNodeValues("//*:searchResult/*:contactId")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")
def nameStanza = responseHolder.getNodeValues("//*:contact/*:name")
def addressStanza = responseHolder.getNodeValues("//*:contact/*:address")
def emailStanza = responseHolder.getNodeValues("//*:contact/*:email")
def telecomStanza = responseHolder.getNodeValues("//*:contact/*:telecommunication")

def preContactIdWeSpt = messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC23_1_CONTACT_ID")
def preContactIdOneSrc = messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC23_2_CONTACT_ID")

boolean recordTypeMatch=false
boolean contactIdMatch=false
boolean finalAssert=false

if (recordTypes.size()==3){
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("GOLDEN")){
			recordTypeMatch=true
			break;
		}
	}
}
int IdCount = 0
if (contactIDs.size()==3){
	for (int x=0; x<contactIDs.size(); x++){
		if (contactIDs[x].toString().equals(preContactIdWeSpt)){
			IdCount++
		}
	}

	for (int x=0; x<contactIDs.size(); x++){
		if (contactIDs[x].toString().equals(preContactIdOneSrc)){
			IdCount++
		}
	}
}
assert IdCount == 2 
assert recordTypeMatch == true
assert nameStanza.size() == 2
assert addressStanza.size() == 2
assert emailStanza.size() == 3
assert telecomStanza.size() == 3

