import org.slf4j.LoggerFactory
import groovy.json.JsonSlurper
import com.eviware.soapui.support.XmlHolder
//------------------------------------------------------------------------------------------------------------------------------
//Saving contactID in custom property:
def requestHolder = new XmlHolder( messageExchange.requestContentAsXml )
def responseHolder = new XmlHolder( messageExchange.responseContentAsXml )
def contactIDs = responseHolder.getNodeValues("//*:searchResult/*:contactId")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")
def preContactIdWeSpt = messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC2_1_CONTACT_ID")
def preContactIdOneSrc = messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC2_2_CONTACT_ID")

boolean recordTypeMatch=false
boolean contactIdMatch=false
boolean finalAssert=false

if (recordTypes.size()==3){
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("GOLDEN")){
			recordTypeMatch=true
			break;
		}
	}
}

int IdCount = 0
if (contactIDs.size()==3){
	for (int x=0; x<contactIDs.size(); x++){
		if (contactIDs[x].toString().equals(preContactIdWeSpt)){
			IdCount++
		}
	}

	for (int x=0; x<contactIDs.size(); x++){
		if (contactIDs[x].toString().equals(preContactIdOneSrc)){
			IdCount++
		}
	}
}

if (IdCount == 2){
	contactIdMatch=true
}

if (recordTypeMatch==true && contactIdMatch==true){
	finalAssert=true
}

assert recordTypeMatch == true

