String socialMediaDesc = listOrgs[0].id.social_media_handle_desc.toString()

String Custom_socialMediaDesc = testRunner.testCase.testSuite.project.getPropertyValue("SOCIAL_MEDIA_VALUE") 
testRunner.testCase.testSuite.project.setPropertyValue("TC25_1_CONTACT_ID",internalPartyId)
log.info("Internal PartyId :" + internalPartyId)
log.info (socialMediaDesc)
dseCluster.close();

FileWriter writer = new FileWriter(new File(System.getProperty("user.dir") + "//Contacts_From_RSP_To_MDM.txt"),true);
writer.write(internalPartyId.toString() + "\n");
writer.close();

