//------------------------------------------------------------------------------------------------------------------------------

def requestHolder = new XmlHolder( messageExchange.requestContentAsXml )
def responseHolder = new XmlHolder( messageExchange.responseContentAsXml )
def contactIDs = responseHolder.getNodeValues("//*:searchResult/*:contactId")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")
def preContactId = messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC1_CONTACT_ID")

boolean recordTypeMatch=false
boolean contactIdMatch=false
boolean finalAssert=false

if (recordTypes.size()==2){
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("GOLDEN")){
			recordTypeMatch=true
		}
	}
}
if (contactIDs.size() == 2)
{
	for (int x=0; x<contactIDs.size(); x++){
		if (contactIDs[x] == preContactId){
			contactIdMatch=true
			break;
		}
	}
}
if (recordTypeMatch==true && contactIdMatch==true){
	finalAssert=true
}
assert finalAssert == true

import javax.jms.*
import groovy.io.*
import java.util.Map
import java.util.List
import org.slf4j.Logger
import java.util.Properties
import javax.naming.Context
import org.slf4j.LoggerFactory
import groovy.json.JsonSlurper
import javax.naming.InitialContext
import com.eviware.soapui.support.XmlHolder
import java.util.concurrent.ExecutionException;
//------------------------------------------------------------------------------------------------------------------------------
//Saving contactID in custom property:
def requestHolder = new XmlHolder( messageExchange.requestContentAsXml )
def responseHolder = new XmlHolder( messageExchange.responseContentAsXml )
def reqFirstName = requestHolder.getNodeValue("//*:personInfo/*:firstName")
def reqLastName = requestHolder.getNodeValue("//*:personInfo/*:lastName")
def resFirstName = responseHolder.getNodeValue("//*:personInfo/*:firstName")
def resLastName = responseHolder.getNodeValue("//*:personInfo/*:lastName")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")

boolean finalAssert=false
int rawRecCount = 0
int goldenRecCount = 0

// Verify the response should contain one Raw record and one Golden record
if (recordTypes.size()==3)
{
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("GOLDEN")){
			goldenRecCount++;
		}
	}
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("ORIGINAL")){
			rawRecCount++;
		}
	}	
}

if (rawRecCount==1 && rawRecCount==2){
	finalAssert=true
}

assert reqFirstName == resFirstName
assert reqLastName == resLastName
assert finalAssert == true

import javax.jms.*
import groovy.io.*
import java.util.Map
import java.util.List
import org.slf4j.Logger
import java.util.Properties
import javax.naming.Context
import org.slf4j.LoggerFactory
import groovy.json.JsonSlurper
import javax.naming.InitialContext
import com.eviware.soapui.support.XmlHolder
import java.util.concurrent.ExecutionException;
//------------------------------------------------------------------------------------------------------------------------------
//Saving contactID in custom property:
def requestHolder = new XmlHolder( messageExchange.requestContentAsXml )
def responseHolder = new XmlHolder( messageExchange.responseContentAsXml )
def reqFirstName = requestHolder.getNodeValue("//*:personInfo/*:firstName")
def reqLastName = requestHolder.getNodeValue("//*:personInfo/*:lastName")
def resFirstName = responseHolder.getNodeValue("//*:personInfo/*:firstName")
def resLastName = responseHolder.getNodeValue("//*:personInfo/*:lastName")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")

boolean rawRecordTypeMatch=false
boolean goldenRecordTypeMatch=false
boolean finalAssert=false

// Verify the response should contain one Raw record and one Golden record
if (recordTypes.size()==2)
{
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("GOLDEN")){
			goldenRecordTypeMatch=true
			break;
		}
	}
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("ORIGINAL")){
			rawRecordTypeMatch=true
			break;
		}
	}	
}
if (rawRecordTypeMatch==true && goldenRecordTypeMatch==true){
	finalAssert=true
}

assert reqFirstName == resFirstName
assert reqLastName == resLastName
assert finalAssert == true

import javax.jms.*
import groovy.io.*
import java.util.Map
import java.util.List
import org.slf4j.Logger
import java.util.Properties
import javax.naming.Context
import org.slf4j.LoggerFactory
import groovy.json.JsonSlurper
import javax.naming.InitialContext
import com.eviware.soapui.support.XmlHolder
import java.util.concurrent.ExecutionException;
//------------------------------------------------------------------------------------------------------------------------------
//Saving contactID in custom property:
def requestHolder = new XmlHolder( messageExchange.requestContentAsXml )
def responseHolder = new XmlHolder( messageExchange.responseContentAsXml )
def reqEmail = requestHolder.getNodeValue("//*:email/*:value")
def resEmails = responseHolder.getNodeValues("//*:email/*:value")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")

boolean goldenRecordTypeMatch=false
boolean isEmailFound=false

// Verify the response should contain one Golden record only
if (recordTypes.size()==1)
{
	if (recordTypes[0].toString().equals("GOLDEN")){
		goldenRecordTypeMatch=true
	}
}

if (resEmails.size()==2)
{
	for(int x=0; x<2; x++){
		if (resEmails[x].toString().equalsIgnoreCase(reqEmail)){
			isEmailFound=true
			break;
		}		
	}
}

assert isEmailFound == true
assert goldenRecordTypeMatch == true

import javax.jms.*
import groovy.io.*
import java.util.Map
import java.util.List
import org.slf4j.Logger
import java.util.Properties
import javax.naming.Context
import org.slf4j.LoggerFactory
import groovy.json.JsonSlurper
import javax.naming.InitialContext
import com.eviware.soapui.support.XmlHolder
import java.util.concurrent.ExecutionException;
//------------------------------------------------------------------------------------------------------------------------------
//Saving contactID in custom property:
def requestHolder = new XmlHolder( messageExchange.requestContentAsXml )
def responseHolder = new XmlHolder( messageExchange.responseContentAsXml )
def reqEmail = requestHolder.getNodeValue("//*:email/*:value")
def resEmails = responseHolder.getNodeValues("//*:email/*:value")
def recordTypes = responseHolder.getNodeValues("//*:searchResult/*:recordType")

boolean rawRecordTypeMatch=false
boolean goldenRecordTypeMatch=false
boolean finalAssert=false
boolean isEmailFound=false

// Verify the response should contain one Raw record and one Golden record
if (recordTypes.size()==2)
{
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("GOLDEN")){
			goldenRecordTypeMatch=true
			break;
		}
	}
	for (int x=0; x<recordTypes.size(); x++){
		if (recordTypes[x].toString().equals("ORIGINAL")){
			rawRecordTypeMatch=true
			break;
		}
	}
}
if (rawRecordTypeMatch==true && goldenRecordTypeMatch==true){
	finalAssert=true
}

// Validate total email count should be 3 i.e. two are from Golden and one from Raw
if (resEmails.size()==3)
{
	for (int x=0; x<resEmails.size(); x++){
		if (resEmails[x].equalsIgnoreCase(reqEmail)){
			isEmailFound=true
			break;
		}
	}	
}
assert isEmailFound == true
assert finalAssert == true

