//DSE CONNECTION:
def DseSession _dseSession;
def GraphTraversalSource g;

def graphName = "contact_graph_level_2";
def username  ="cassandra";

DseCluster dseCluster = DseCluster.builder()
                .addContactPoint("cassdb125.fsc.fedex.com")
                .withSSL().withCredentials(username, username)
                .withGraphOptions(new GraphOptions()
                        .setGraphName(graphName)
                        .setGraphSubProtocol(GraphProtocol.GRAPHSON_2_0))
                .build();

_dseSession = dseCluster.connect();
g = DseGraph.traversal(_dseSession);

