import javax.jms.*
import groovy.io.*
import com.eviware.soapui.support.XmlHolder
//------------------------------------------------------------------------------------------------------------------------------
def responseHolder = new XmlHolder( messageExchange.responseContentAsXml )
def entityType = responseHolder.getNodeValue("//*:relationshipDetail/*:entityType")
def relationshipType = responseHolder.getNodeValue("//*:relationshipDetail/*:relationshipType")
def entityValue = responseHolder.getNodeValue("//*:relationshipDetail/*:entityValue")
def companyName = responseHolder.getNodeValues("//*:additionalRelationshipInfo/*:value")
boolean isMatchCompany = false
String cname = ""
if (companyName.size() > 0){
	for (int i=0; i<companyName.size(); i++){
		cname = companyName[i].toString().replace(" ","")
		if (cname.equals(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("COMPANY_NAME")))
			isMatchCompany=true;
	}
}
assert entityType == messageExchange.modelItem.testStep.testCase.project.getPropertyValue("ENTITY_TYPE")
assert relationshipType == messageExchange.modelItem.testStep.testCase.project.getPropertyValue("REL_TYPE")
assert entityValue == messageExchange.modelItem.testStep.testCase.project.getPropertyValue("ENTITY_VALUE")
assert isMatchCompany == true

