import javax.jms.*
import groovy.io.*
import com.eviware.soapui.support.XmlHolder
//------------------------------------------------------------------------------------------------------------------------------
def responseHolder = new XmlHolder( messageExchange.responseContentAsXml )
def entityValue = responseHolder.getNodeValues("//*:relationshipDetail/*:entityValue")
def relationshipTypes = responseHolder.getNodeValues("//*:relationshipDetail/*:relationshipType")
def companyName = responseHolder.getNodeValues("//*:additionalRelationshipInfo/*:value")
boolean isMatchCompany = false
boolean isEntityValueMatched = false
String cname = ""
if (companyName.size() > 0){
	for (int i=0; i<companyName.size(); i++){
		cname = companyName[i].toString().replace(" ","")
		if (cname.equals(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("COMPANY_NAME")))
			isMatchCompany=true;
	}
}
if (entityValue.size() > 0){ // Account No verification
	for (int i=0; i<entityValue.size(); i++){
		if (entityValue[i].toString().equals(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("ENTITY_VALUE")))
			isEntityValueMatched = true;
	}
}
ArrayList<String> reqRelTypes = new ArrayList<String>()
reqRelTypes.add(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC2_RELTYPE_PBA"))
reqRelTypes.add(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC2_RELTYPE_PA"))
Collections.sort(reqRelTypes)

ArrayList<String> resRelTypes = new ArrayList<String>()
resRelTypes.add(relationshipTypes[0])
resRelTypes.add(relationshipTypes[1])
Collections.sort(resRelTypes)

assert isEntityValueMatched == true
assert reqRelTypes == resRelTypes
assert isMatchCompany == true

