reqEntityTypes.add(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC5_ENTITY_TYPE_ORG"))
reqEntityTypes.add(messageExchange.modelItem.testStep.testCase.project.getPropertyValue("TC5_ENTITY_TYPE_ACC"))
Collections.sort(reqEntityTypes)

