resEntityTypes.add(entityTypes[0])
resEntityTypes.add(entityTypes[1])
Collections.sort(resEntityTypes)

assert reqEntityTypes == resEntityTypes
assert reqRelTypes == resRelTypes
assert isMatchCompany == true
assert entityValue == messageExchange.modelItem.testStep.testCase.project.getPropertyValue("ENTITY_VALUE")

