readContactIDs()

// Script that compares the Landing Table count with captured ContactIDs
public void readContactIDs()
{
	def filePath = System.getProperty("user.dir") + "//Contacts_From_WebService_To_MDM.txt"
	BufferedReader reader;
	contactsCount=0
	ArrayList<String> contactIDs = new ArrayList<String>();
	try{
		reader = new BufferedReader(new FileReader(filePath.toString().trim()))
		String line = reader.readLine()
		while (line != null){
			contactsCount++
			line = reader.readLine()
		}
		reader.close()
	}
	catch(IOException ex){
		e.printStackTrace()
	}

	int matchingCount = Integer.parseInt(testRunner.testCase.testSuite.project.getPropertyValue("MATCHING_CONTACT_ID_COUNT"))
	if (contactsCount == matchingCount){
		log.info("PASS " + "Counts from Contacts:" + contactsCount + " MDM Received Count:" + matchingCount)
	}
	else{
		log.info("FAIL " + "Counts from Contacts:" + contactsCount + " MDM Received Count:" + matchingCount)
	}
}

