deleteContactsById()

// Script that deletes the Contacts information by IDs from the files
public void deleteContactsById()
{
	String [] fileNames = new String[2]
	fileNames[0] = "Contacts_From_WebService_To_MDM.txt"
	fileNames[1] = "Contacts_From_RSP_To_MDM.txt"

	for (int x=0; x<fileNames.length; x++)
	{
		def filePath = System.getProperty("user.dir") + "\\" + fileNames[x]
		File file = new File(filePath)
		log.info(filePath)

		if (!file.exists())
		continue;

		BufferedReader reader;
		def runner = null
		if (fileNames[x].equals("Contacts_From_WebService_To_MDM.txt"))
		{
			testRunner.testCase.testSuite.project.setPropertyValue("DEL_SRCAPP_ID","3531347")
			testRunner.testCase.testSuite.project.setPropertyValue("DEL_ORGINSRC_APPID","3530874")
		}
		else
		{
			testRunner.testCase.testSuite.project.setPropertyValue("DEL_SRCAPP_ID","105699")
			testRunner.testCase.testSuite.project.setPropertyValue("DEL_ORGINSRC_APPID","105699")			
		}

		try{
			reader = new BufferedReader(new FileReader(filePath.toString()))
			String line = ""
			int i=1
			while ((line = reader.readLine()) != null)
			{			 
				def projectName = testRunner.testCase.testSuite.project
				def suiteName = testRunner.testCase.testSuite.name
				def tc = testRunner.testCase.name
				tc = projectName.testSuites[suiteName].testCases[tc]
				def tstp = tc.getTestStepByName('TSTP_DELETE_CONTACT_BY_CONTACT_ID')
				testRunner.testCase.testSuite.project.setPropertyValue("DELETE_CONTACT_ID",line.toString())
				runner = tstp.run(testRunner,context)
				sleep(500)
				runner = null
				log.info(line.toString() + " deleted.")
				i++
			}
			reader.close()
		}
		catch(IOException ex){
			ex.printStackTrace()
		}
	}
}

